## The Basics

    var H = new Marky(document.body)

    with(H) {
      h2("Form")

      form({action: '/index.html', method: 'get'}, function() {
        label("Name:", function() {
          input({placeholder: 'type'})
        })
        label("Age:", function() {
          select(function() {
            [37, 39, 45, 34].forEach(function(age) {
              var attrs = {value: age}
              if (age == 45) {
	        attrs.selected = 'selected'
	      }
	      option(age, attrs)
            })
          })
        })
        p(function() {
          button("Submit", {type: 'submit'})
          a("Cancel", {href: 'http://www.google.com'})
        })
      })
    }

    // Or ...

    H.h2(function(){
      H.a("Github", {href: 'http://www.github.com'})
    })

